<script>
import gsap from 'gsap';

export default {
  name: 'LogoText',
  methods: {
    animateSVG() {
      const symbols = this.$refs?.logoTextSymbols?.children;
      const mainText = this.$refs?.logoTextMain?.children;
      gsap.set(this.$refs.logoTextWrapper, { strokeDasharray: 120, strokeDashoffset: 120 });
      mainText.forEach((path, idx) => {
        const delay = idx * 0.2;
        const duration = 1;
        gsap.to(path, { strokeDashoffset: 0, delay, duration, ease: 'ease-in' });
        gsap.to(path, {
          fill: 'white',
          delay: delay / 2 + duration,
          duration: 1,
          ease: 'ease-in',
          stroke: 'transparent',
        });
      });
      symbols.forEach((path, idx) => {
        const delay = 2 + idx * 0.07;
        const duration = 1;
        gsap.to(path, { strokeDashoffset: 0, delay, duration, ease: 'ease-in' });
        gsap.to(path, {
          fill: '#ceccc5',
          delay: delay / 1.5 + duration,
          duration: 1,
          ease: 'ease-in',
          stroke: 'transparent',
        });
      });
    },
  },
  mounted() {
    this.animateSVG();
  },
};
</script>

